'use strict';

function containsExpandableRows(table) {
  return table.querySelector('dx-table-row *[slot="content"]') != null;
}
function showStickyContainer(tableScrollable, showExpandColumn, showSelectionColumn, mediaQueryAllowsStickyness, isStickySlotDefined) {
  if (!tableScrollable) {
    return false;
  }
  const defaultStickyColumnsVisible = showExpandColumn || showSelectionColumn;
  return (defaultStickyColumnsVisible ||
    (mediaQueryAllowsStickyness && isStickySlotDefined()));
}

exports.containsExpandableRows = containsExpandableRows;
exports.showStickyContainer = showStickyContainer;
