/**
 *@returns false If object holds another object that has not the same reference the function will return false.
 *@returns false If one of the objects is null or undefined.
 *@returns false If one of the objects has more or less properties.
 */
export function compareObjectsEqual(a, b) {
  if (a === b) {
    return true;
  }
  if (a == null || b == null) {
    return false;
  }
  return (Object.getOwnPropertyNames(a).every((propA) => a[propA] === b[propA]) &&
    Object.getOwnPropertyNames(b).every((propB) => b[propB] === a[propB]));
}
