import { h, Host } from "@stencil/core";
/**
 * Centers the application in the middle of the screen. The component is responsive and implements margins to the edge of the window.
 * Have to be used only in an `<ds-app>` component.
 *
 * @path /Scaffold
 * @name Page
 * @icon file
 * @stable
 */
export class DsPage {
  render() {
    return (h(Host, null, h("div", { class: "page-wrapped-content" }, h("slot", null))));
  }
  static get is() { return "ds-page"; }
  static get encapsulation() { return "shadow"; }
  static get originalStyleUrls() {
    return {
      "$": ["ds-page.scss"]
    };
  }
  static get styleUrls() {
    return {
      "$": ["ds-page.css"]
    };
  }
}
