import { h } from "@stencil/core";
// TODO: Render with icon when accessible
export const ReadonlyTextElement = (props) => [
  h("dx-input-label", { label: props.label }),
  h("dx-text", { type: "ps", color: "headline" }, props.text),
];
export const ReadonlyIconElement = (props) => (h("dx-icon", { style: { "margin-left": "6px" }, icon: props.icon, size: 16 }));
export const BaselineElement = (props) => props.onlyDiv ? (h("div", null)) : (h("dx-text", { class: "baseline-text", style: {
    visibility: "hidden",
    width: "0",
    display: "inline-block",
  } }, "b"));
