import { h, Host } from "@stencil/core";
import { ColorPalette } from "../../../classes/color-palette";
/**
 * Base component for a the site layout.
 * This component sets the background-color and removes the standard margins of the html body.
 *
 * @path /Scaffold
 * @name App base
 * @shortname App
 * @icon chalkboard-teacher
 * @stable
 *
 */
export class DxApp {
  componentWillLoad() {
    this.styleBody();
  }
  styleBody() {
    const bodyStyle = document.body.style;
    bodyStyle.background = new ColorPalette(true).getColorCode("gray-7");
    bodyStyle.margin = "0";
  }
  render() {
    return (h(Host, null, h("slot", null)));
  }
  static get is() { return "dx-app"; }
  static get encapsulation() { return "shadow"; }
  static get originalStyleUrls() {
    return {
      "$": ["dx-app.scss"]
    };
  }
  static get styleUrls() {
    return {
      "$": ["dx-app.css"]
    };
  }
}
