/**
 * A form wrapper component which adds additional states to a slotted block.
 * It wraps content into a labeled container with an optional required asterisk within the label and an optional error container with a message.
 *
 * @name Form Wrapper
 * @shortname Wrapper
 * @path /Form
 * @icon align-justify
 * @stable
 *
 * @slot error-message - This slot can be defined with a `<dx-error-message>` element.
 */
export declare class DxFormWrapper {
  self: HTMLDxFormWrapperElement;
  /**
   * Label which is displayed above the slotted content.
   * @important
   * @value string: Label e.g `first name`
   * @example label
   */
  label: string;
  /**
   * An asterisk next to the label indicates that the wrapped form element is required.
   *
   * @value false: Not required.
   * @value true: Required.
   * @example required
   */
  required: boolean;
  /**
   * Indicates that the form is synced with KI.
   * @value true: Synchronized.
   * @value false: Not synchronized.
   * @example kisynced
   */
  kisynced: boolean;
  /**
   * Error text which is displayed below the slotted content.
   * @value string: error message
   * @example error
   */
  errormessage: string;
  componentDidLoad(): void;
  onForceUpdateOnChange(e: CustomEvent): void;
  render(): any;
}
