// Load design system
import { defineCustomElements as loadDesignSystem } from "@dvag/design-system/loader";

loadDesignSystem().then();

function createFontLink() {
  const fontLink = document.createElement("link");
  fontLink.rel = "stylesheet";
  fontLink.href = "design-system-css/design-system.css";
  return fontLink;
}

// Load Design System CSS
document.querySelector("head").appendChild(createFontLink());

// Append to body
document.body.innerHTML = `

<ds-text type="u1">Beispiel Anwendung</ds-text>
<dx-text type="h1">Beispiel Anwendung</dx-text>

<br><br>

<ds-button type="primary" size="medium">
    <ds-text>Ein Button</ds-text>
</ds-button>

<br><br>

<ds-function-icon-button>
    <ds-icon icon="einstellungen" size="72"></ds-icon>
</ds-function-icon-button>

<br><br>

<ds-slider min="0" max="100" value="50" formattedvalue="$1 %" label="Prozentangabe"></ds-slider>

`;
